#include <iostream>#include <unordered_map>/*Input: aafbaaaaffcOutput: a:4 b:1 f:2 c:1*/// "aaa"// ""// "aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa" = length = MAX_INT// ""// "aaabaabaaaa"// "aaaabaaabaaa"// "aabaaa"// char* stc;void count(const std::string& st, std::unordered_map<char, size_t>& mp){    if (st.empty())        return;    char cur = st[0];    size_t cur_count = 1;    for (size_t sz = st.length(), i = 1; i < sz; ++i)    {        if (st[i] == cur)            ++cur_count;        else        {            mp[cur] = std::max(mp[cur], cur_count);            cur_count = 1;            cur = st[i];        }    }    mp[cur] = std::max(mp[cur], cur_count);}int main() {        std::unordered_map<char, size_t> mp;    count(std::string("aafbaaaaffc"), mp);    for (/*const*/ auto& [ch, count] : mp)    {    }    std::cout << "Hello, VK!" << std::endl;    return 0;}